mapper.select:
SELECT AA.* FROM( SELECT t.reportId, t. WEEK, DATE_FORMAT(t.createTime, '%m月%d日') createTime, DATE_FORMAT(t.createTime, '%H:%i') timePoint, t.createBy AS userId, u.userName, t.deptId, d.deptName, dic.label AS postName, t.visitLine, t.lineStoreNum, t.visitStoreNum,t.updateTime FROM tbl_user_daily_t t LEFT JOIN salesman u ON u.id = t.createBy LEFT JOIN tbl_user_dept_t ud ON ud.userId = t.createBy LEFT JOIN tbl_department_t d ON d.deptId = ud.deptId LEFT JOIN tbl_dict_t dic ON u.postType = dic.`value` AND dic.type = 'post_type' AND dic.isDelete=0 WHERE t.deptId = #{deptId}
and DATE_FORMAT(t.createTime, '%Y-%m-%d')=DATE_FORMAT(#{createTime}, '%Y-%m-%d')
and t.createBy = #{userId}
UNION SELECT t.reportId, t. WEEK, DATE_FORMAT(t.createTime, '%m月%d日') createTime, DATE_FORMAT(t.createTime, '%H:%i') timePoint, t.createBy AS userId, u.userName, t.deptId, d.deptName, dic.label AS postName, t.visitLine, t.lineStoreNum, t.visitStoreNum,t.updateTime FROM tbl_user_daily_t t LEFT JOIN salesman u ON u.id = t.createBy LEFT JOIN tbl_user_dept_t ud ON ud.userId = t.createBy LEFT JOIN tbl_department_t d ON d.deptId = ud.deptId LEFT JOIN tbl_dict_t dic ON u.postType = dic.`value` AND dic.type = 'post_type' AND dic.isDelete=0 where t.deptId != #{deptId} AND t.reportId in(${reportId})
) AA ORDER BY AA.updateTime desc

SELECT t.reportId, t. WEEK, DATE_FORMAT(t.createTime, '%m月%d日') createTime, DATE_FORMAT(t.createTime, '%H:%i') timePoint, t.createBy AS userId, u.userName, t.deptId, d.deptName, dic.label AS postName, t.visitLine, t.lineStoreNum, t.visitStoreNum FROM tbl_user_daily_t t LEFT JOIN salesman u ON u.id = t.createBy LEFT JOIN tbl_user_dept_t ud ON ud.userId = t.createBy LEFT JOIN tbl_department_t d ON d.deptId = ud.deptId LEFT JOIN tbl_dict_t dic ON u.postType = dic.`value` AND dic.type = 'post_type' AND dic.isDelete=0 WHERE t.deptId = #{deptId}
and DATE_FORMAT(t.createTime, '%Y-%m-%d')=DATE_FORMAT(#{createTime}, '%Y-%m-%d')
and t.createBy = #{userId}
ORDER BY t.updateTime desc

SELECT t.reportId, t. WEEK, DATE_FORMAT(t.createTime, '%m月%d日') createTime, DATE_FORMAT(t.createTime, '%H:%i') timePoint, t.createBy AS userId, u.userName, t.deptId, d.deptName, dic.label AS postName, t.visitLine, t.lineStoreNum, t.visitStoreNum FROM tbl_user_daily_t t LEFT JOIN salesman u ON u.id = t.createBy LEFT JOIN tbl_user_dept_t ud ON ud.userId = t.createBy LEFT JOIN tbl_department_t d ON d.deptId = ud.deptId LEFT JOIN tbl_dict_t dic ON u.postType = dic.`value` AND dic.type = 'post_type' AND dic.isDelete=0 WHERE 1 = 1
and DATE_FORMAT(t.createTime, '%Y-%m-%d')=DATE_FORMAT(#{createTime}, '%Y-%m-%d')
and t.deptId = #{deptId}
and t.createBy = #{userId}
ORDER BY t.updateTime desc,t.createTime desc
